import router from 'next/router';
import React, { useEffect } from 'react';
import Layout from '@components/ui/Layout';

import Home from './home';
import Exhibition from './exhibition';

const Index = () => {
    const [route, setRoute] = React.useState<boolean>(false);

    const routes = router.pathname;

    useEffect(() => {
        if (sessionStorage.length === 0) {
            router.push('/login');
        }
    });
    useEffect(() => {
        if (routes === 'exhibition') {
            router.push('/exhibition');
        } else {
            router.push('/');
        }
    });

    return (
        <Layout>
            <div className=" w-full h-auto bg-red-100">
                <div className="flex justify-center ">{route ? <Home /> : <Exhibition />}</div>
            </div>
            <div className="w-full h-52 bg-red-100"></div>
        </Layout>
    );
};

export default Index;
