import React, { FC } from 'react';
import TextMenu from '@components/Car';

import UserIcon from '@assets/svg/Profile.svg';
import EditIcon from '@assets/svg/menu-svg/Edit.svg';
import LoginIcon from '@assets/svg/menu-svg/Login.svg';
import PlayIcon from '@assets/svg/menu-svg/Play.svg';
import PlusIcon from '@assets/svg/menu-svg/Plus.svg';
import TicketIcon from '@assets/svg/menu-svg/Ticket.svg';
import TimeIcon from '@assets/svg/menu-svg/Time.svg';
import UsersIcon from '@assets/svg/menu-svg/User.svg';
import WorkIcon from '@assets/svg/menu-svg/Work.svg';

const textMenu = [
    { title: 'اطلاعات سطح', icons: <UserIcon /> },
    { title: 'ویرایش مشخصات', icons: <EditIcon /> },
    { title: 'اگهی های منتخب', icons: <TicketIcon /> },
    { title: 'اگهی های در انتظار', icons: <TimeIcon /> },
    { title: 'ثبت تبلیغات', icons: <PlusIcon /> },
    { title: 'معرفی به دوستان', icons: <UsersIcon /> },
    { title: 'قوانین مقررات', icons: <WorkIcon /> },
    { title: 'فیلم آموزشی برنامه', icons: <PlayIcon /> },
    { title: 'خروج از حساب کاربری', icons: <LoginIcon /> },
];

interface Props {
    menu?: boolean;
    handleClick?: any;
}

const index: FC<Props> = ({ menu, handleClick }) => {
    return (
        <div className="">
            <div
                onClick={handleClick}
                className={menu ? `w-full h-screen bg-blacks-100 z-20 absolute opacity-80` : ''}
            ></div>
            {
                <div
                    className={` ${
                        menu
                            ? ' transition-all duration-300 w-72 bg-white'
                            : ' transition-all duration-300 w-0'
                    }   h-screen z-50 absolute w-0 p-3`}
                >
                    {menu ? (
                        <div className="">
                            <div className="p-3">
                                <div className="flex justify-around">
                                    <div className="w-16 h-16 bg-red-50 rounded-full"></div>
                                    <div className="text-sm">
                                        <p>نمایشگاه احمدی</p>
                                        <p>کد نمایندگی : whh5476</p>
                                        <p>09104866595</p>
                                    </div>
                                </div>
                            </div>

                            <div className="mt-3 flex bg-gradient-to-l from-red-50 p-2 mr-1">
                                <div className="-mr-4 bg-blue-300 w-1 h-10 absolute -mt-2"></div>
                                <UserIcon className="w-5 h-5" />
                                <p className="mr-5">اطلاعات صلح</p>
                            </div>
                        </div>
                    ) : null}
                </div>
            }
        </div>
    );
};

export default index;
