import React, { FC } from 'react';

interface Props {
    menu?: boolean;
    handleMenu?: any;
}

const index: FC<Props> = ({ menu, handleMenu }) => {
    console.log(handleMenu);
    return (
        <div>
            <div
                className={` ${
                    menu
                        ? ' transition-all duration-300 w-72'
                        : ' transition-all duration-300 -w-72'
                }   h-screen bg-white z-50 absolute w-0 p-3`}
            >
                <div className="flex justify-end ml-5">
                    <div className="bg-red-50 rounded-full w-5 h-5 flex justify-center items-center text-white">
                        X
                    </div>
                </div>
            </div>
        </div>
    );
};

export default index;
