import React, { FC } from 'react';

interface Props {
    show?: boolean;
    handleClick?: any;
}

const index: FC<Props> = ({ show, handleClick }) => {
    return (
        <div>
            {show ? (
                <div className={` lg:hidden md:hidden block w-full h-screen top-0 fixed z-50`}>
                    <div className="">
                        <div className="w-full h-12 bg-gray-400 flex justify-end p-3">
                            <div
                                onClick={handleClick}
                                className="text-white bg-red-600 h-6 w-6 rounded-full flex justify-center items-center"
                            >
                                X
                            </div>
                        </div>
                        <hr />
                        <div className="w-full h-screen bg-red-300"></div>
                    </div>
                </div>
            ) : null}
        </div>
    );
};

export default index;
