import React, { FC } from 'react';
import Filter from './filter';

interface Props {
    show?: boolean;
}

const index: FC<Props> = ({ show }) => {
    return (
        <>
            {show ? (
                <div
                    className={` ${
                        show
                            ? 'transform transition-all duration-1000 w-52 h-auto bg-white p-3'
                            : 'transform transition-all duration-1000 w-0 h-auto'
                    } row-span-1 rounded-lg  `}
                >
                    {Filter.map((filter, index) => (
                        <div className="text-xs" key={index}>
                            <div className="flex mt-1">
                                <div className="mt-1">{filter.icon}</div>
                                <p className="mr-1 mt-2">{filter.title}</p>
                            </div>
                            <div
                                className={`${filter.line ? `h-11 bg-red-50 w-full` : ''}   mt-3`}
                            ></div>
                            <div className="flex justify-between">
                                <p className="mr-8 -mt-2">{filter.name}</p>
                                <span className="-mt-1">{filter.arrow}</span>
                            </div>
                            <div className={` bg-watusi-250 w-full `} style={{ height: '1px' }} />
                        </div>
                    ))}
                </div>
            ) : null}
        </>
    );
};

export default index;
