import React, { FC } from 'react';
import UserIcon from '@assets/svg/Profile.svg';

interface Props {
    menu?: boolean;
    handleClick?: any;
}

const index: FC<Props> = ({ menu, handleClick }) => {
    const [sideBar, setSideBar] = React.useState(false);

    return (
        <div className="">
            <div
                onClick={handleClick}
                className={menu ? `w-full h-screen bg-blacks-100 z-20 absolute opacity-80` : ''}
            ></div>
            {
                <div
                    className={` ${
                        menu
                            ? ' transition-all duration-300 w-72 bg-white'
                            : ' transition-all duration-300 w-0'
                    }   h-screen z-50 absolute w-0 p-3`}
                >
                    {menu ? (
                        <div className="">
                            <div className="p-3">
                                <div className="flex justify-around">
                                    <div className="w-16 h-16 bg-red-50 rounded-full"></div>
                                    <div className="text-sm">
                                        <p>نمایشگاه احمدی</p>
                                        <p>کد نمایندگی : whh5476</p>
                                        <p>09104866595</p>
                                    </div>
                                </div>
                            </div>
                            <div className="flex">
                                <div className="w-1 h-10 bg-red-50"></div>
                                <div className="mt-3 flex bg-gradient-to-l from-red-50 p-2 mr-2">
                                    <UserIcon className="w-5 h-5" />
                                    <p className="mr-5">اطلاعات صلح</p>
                                </div>
                            </div>
                        </div>
                    ) : null}
                </div>
            }
        </div>
    );
};

export default index;
