import React, { FC } from 'react';

interface Props {
    menu?: boolean;
    handleClick?: any;
}

const index: FC<Props> = ({ menu, handleClick }) => {
    return (
        <div className="w-full h-screen bg-red-500">
            <div
                className={` ${
                    menu
                        ? ' transition-all duration-300 w-72 bg-white'
                        : ' transition-all duration-300 w-0'
                }   h-screen z-50 absolute w-0 p-3`}
            >
                <div className="flex justify-end ml-5">
                    <div
                        className="bg-red-50 rounded-full w-5 h-5 flex justify-center items-center text-white cursor-pointer"
                        onClick={handleClick}
                    >
                        X
                    </div>
                </div>
            </div>
        </div>
    );
};

export default index;
