import React, { FC } from 'react';

interface Props {
    menu?: boolean;
}

const index: FC<Props> = ({ menu }) => {
    const [menus, setMenus] = React.useState(menu);

    console.log(menus);

    return (
        <div>
            <div
                className={` ${
                    !menus
                        ? ' transition-all duration-300 w-72'
                        : ' transition-all duration-300 -w-72'
                }   h-screen bg-white z-50 absolute w-0 p-3`}
            >
                <div className="flex justify-end ml-5">
                    <div
                        className="bg-red-50 rounded-full w-5 h-5 flex justify-center items-center text-white"
                        onClick={() => setMenus(!menus)}
                    >
                        X
                    </div>
                </div>
            </div>
        </div>
    );
};

export default index;
